templates.template:
for i = 0..${n}-1, for j=0..${n}-1 {
	${node}[i].${out}[j] --> ${node}[j].${in}[i] if i!=j;
};${cursor}

for i = 0..${n}-1, for j=0..${n}-1 {
	${node}[i].${out}[j] --> ${node}[j].${in}[i] if i!=j && uniform(0,1) < ${connectedness};
};${cursor}

for i=0..${height}-1, for j=0..${width}-1 {
	${node}[i*${width}+j].${down} --> ${node}[(i+1)*${width}+j].${up} if i!=${height}-1;
	${node}[i*${width}+j].${right} --> ${node}[i*${width}+j+1].${left} if j!=${width}-1;
};${cursor}

for i = 0 .. ${rows}*${cols}-1 {
	${node}[i].${n} <-- ${node}[i-2*${cols}].${s} if i+1>2*${cols};
	${node}[i].${nw} <-- ${node}[${cols}-(i/${cols}+1)%2].${se} if i%(2*${cols})!=0 && i+1>${cols};
	${node}[i].${sw} <-- ${node}[i+${cols}-1+(i/${cols})%2].${ne} if i%(2*${cols})!=0 && i<${rows}*${cols}-${cols};
};${cursor}
