package org.omnetpp.scave.model;

import org.eclipse.core.runtime.Assert;
import org.omnetpp.scave.engine.ResultFileManager;

// TODO: delete, use the one generated by SWIG
public enum ResultType {
    SCALAR(ResultFileManager.SCALAR),
    VECTOR(ResultFileManager.VECTOR),
    HISTOGRAM(ResultFileManager.HISTOGRAM),
    STATISTICS(ResultFileManager.STATISTICS);

    int value;

    private ResultType(int v) {
        value = v;
    }

    public int getValue() {
        return value;
    }

    public static ResultType asResultType(int internalResultType) {
        if (internalResultType == ResultFileManager.SCALAR)
            return SCALAR;
        else if (internalResultType == ResultFileManager.VECTOR)
            return VECTOR;
        else if (internalResultType == ResultFileManager.STATISTICS)
            return STATISTICS;
        else if (internalResultType == ResultFileManager.HISTOGRAM)
            return HISTOGRAM;
        else {
            Assert.isTrue(false, "Unknown internal ResultType:"+internalResultType);
            return null;
        }
    }

    /**
     *
     */
    public static int asInternalResultType(ResultType type) {
        switch (type) {
        case SCALAR: return ResultFileManager.SCALAR;
        case VECTOR: return ResultFileManager.VECTOR;
        case STATISTICS: return ResultFileManager.STATISTICS;
        case HISTOGRAM: return ResultFileManager.HISTOGRAM;
        default: Assert.isTrue(false, "Unknown ResultType:"+type); return 0;
        }
    }
}
